        } catch {
            this.setState({error : "Can't find anything."});
        } finally {
            this.setState({ loading:false, result });
        }
    }

    render() {
        const { result, error, loading } = this.state;
        return (
            <DetailPresenter 
                result={result} 
                error={error}
                loading={loading}
            />
        )
    };
}
