import axios from "axios";

const api = axios.create({
    baseURL: "https://api.themoviedb.org/3/",
    params: {
        api_key: "e9feeb75c5355f39efd0fda6478cab35",
        language : "en-US"
    }
});

export const TVapi = {
    nowPlaying: () => api.get("movie/now_playing"),
    upcoming: () => {
        api.get("movie/upcoming");
    },

}

